(14, 1, '2022-09-22 09:26:21', '2022-09-22 09:26:21', '<!-- wp:acf/page-welcome-title {\"id\":\"block_632c490945da0\",\"name\":\"acf/page-welcome-title\",\"data\":{\"title_headline\":\"Register or Renew your\",\"_title_headline\":\"field_632c485b3cb0e\",\"main_title\":\"SAM.gov Registration\",\"_main_title\":\"field_632c48683cb0f\",\"description\":\"A SAM registration is required for any entity to bid on and get paid for federal contracts or to receive federal funds. These include for profit businesses, nonprofits, government contractors, government subcontractors, state governments, and local municipalities.\",\"_description\":\"field_632c48733cb10\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:acf/icon-box-block {\"id\":\"block_632c4173bab66\",\"name\":\"acf/icon-box-block\",\"data\":{\"image\":50,\"_image\":\"field_632c3ed88013a\",\"title\":\"Start a New\\u003cbr\\u003eSAM.gov Registration\",\"_title\":\"field_632c4289c5dea\",\"subtitle\":\"Start a new System for Award Management (SAM) Registration in order to work as a federal contractor or receive business or nonprofit grants. Once approved, your SAM Registration will be valid for one year.\",\"_subtitle\":\"field_632c4290c5deb\",\"button_text\":\"New Registration\",\"_button_text\":\"field_632c429bc5dec\",\"button_url\":\"javascript:;\",\"_button_url\":\"field_632c42a5c5ded\"},\"align\":\"\",\"mode\":\"preview\"} /--></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:acf/icon-box-block {\"id\":\"block_632c4475bee0d\",\"name\":\"acf/icon-box-block\",\"data\":{\"image\":58,\"_image\":\"field_632c3ed88013a\",\"title\":\"Renew a\\u003cbr\\u003eSAM.gov Registration\",\"_title\":\"field_632c4289c5dea\",\"subtitle\":\"A System for Award Management (SAM) Registration must be renewed each year to remain active and compliant. USFCR recommends renewing 90 days prior to the SAM expiration date to avoid service interruptions and penalties.\",\"_subtitle\":\"field_632c4290c5deb\",\"button_text\":\"Renew Registration\",\"_button_text\":\"field_632c429bc5dec\",\"button_url\":\"javascript:;\",\"_button_url\":\"field_632c42a5c5ded\"},\"align\":\"\",\"mode\":\"preview\"} /--></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:acf/help-box-block {\"id\":\"block_632c466cae34c\",\"name\":\"acf/help-box-block\",\"data\":{\"title\":\"Federal Government Advisors Are Here to Help!\",\"_title\":\"field_632c4636fdadd\",\"subtitle\":\"Let us know if you have any questions or would like our assistance with federal registrations and contracting.\",\"_subtitle\":\"field_632c463dfdade\",\"background_image\":65,\"_background_image\":\"field_632c46ac91782\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:shortcode -->\n[business_contacts]\n<!-- /wp:shortcode -->', '', '', 'publish', 'closed', 'closed', '', 'home-page', '', '', '2022-09-22 13:29:45', '2022-09-22 13:29:45', '', 0, 'https://webbydemo.in/wordpress/sam/?page_id=14', 0, 'page', '', 0),
